import { Fragment, useState, useContext } from "react";
import Header from "./components/Layout/Header";
import Meals from "./components/Meals/Meals";
import Cart from "./components/Cart/Cart";
import CartItemContext from "./context/cart-item-context";

function App() {

 /*
  I need to update the totalQuantity based on what we get
  from our cartItemContext inputValue.

  I can use a useReducer which will handle both the individual
  quantity as well as the total quantity, and our object itself.
  This useReducer can go in our context so we can use it anywhere.
  */
  const [isCartVisible, setIsCartVisible] = useState(false);
  const quantityContext = useContext(CartItemContext);

  const showCartHandler = () => {
    if (quantityContext.totalQuantity > 0) {
      setIsCartVisible(true);
    }
  };

  const hideCartHandler = () => {
    setIsCartVisible(false);
  };

  return (
    <Fragment>
      {isCartVisible && <Cart onClose={hideCartHandler}></Cart>}
      <Header/>
      <main>
        <Meals />
      </main>
    </Fragment>
  );
}

export default App;
