import { useRef, useState } from "react";
import classes from "./CheckoutForm.module.css";

const isEmpty = (value) => value.trim() === "";
const hasFiveDigits = (value) => value.trim().length === 5;

const CheckoutForm = (props) => {
  const {
    enteredValue: enteredName,
    isTouched: nameIsTouched,
    inputIsValid: nameIsValid,
    hasError: nameHasError,
    inputChangeHandler: nameChangeHandler,
    inputOnBlurHandler: nameOnBlurHandler,
    reset: resetName,
  } = useInput(isEmpty);

  const {
    enteredValue: enteredStreet,
    isTouched: streetIsTouched,
    inputIsValid: streetIsValid,
    hasError: streetHasError,
    inputChangeHandler: streetChangeHandler,
    inputOnBlurHandler: streetOnBlurHandler,
    reset: resetStreet,
  } = useInput(isEmpty);

  const {
    enteredValue: enteredCity,
    isTouched: cityIsTouched,
    inputIsValid: cityIsValid,
    hasError: cityHasError,
    inputChangeHandler: cityChangeHandler,
    inputOnBlurHandler: cityOnBlurHandler,
    reset: resetCity,
  } = useInput(isEmpty);

  const {
    enteredValue: enteredZip,
    isTouched: zipIsTouched,
    inputIsValid: zipIsValid,
    hasError: zipHasError,
    inputChangeHandler: zipChangeHandler,
    inputOnBlurHandler: zipOnBlurHandler,
    reset: resetZip,
  } = useInput(hasFiveDigits);


  const confirmHandler = (event) => {
    event.preventDefault();

    const formIsValid =
      nameIsValid &&
      streetIsValid &&
      zipIsValid &&
      cityIsValid;

    if (!formIsValid) {
      return;
    }

    //submit cart data
    resetName('');
    resetStreet('');
    resetCity('');
    resetZip('');
  };

  const nameControlClasses = `${classes.control} ${
    nameIsValid ? "" : classes.invalid
  }`;
  const streetControlClasses = `${classes.control} ${
    streetIsValid ? "" : classes.invalid
  }`;
  const zipControlClasses = `${classes.control} ${
    zipIsValid ? "" : classes.invalid
  }`;
  const cityControlClasses = `${classes.control} ${
    cityIsValid ? "" : classes.invalid
  }`;

  return (
    <form className={classes.form} onSubmit={confirmHandler}>
      <div className={nameControlClasses}>
        <label htmlFor="name">Your Name</label>
        <input type="text" id="name" />
        {!formInputsValidity.name && <p>Name cannot be empty!</p>}
      </div>
      <div className={streetControlClasses}>
        <label htmlFor="street">Street</label>
        <input type="text" id="street" />
        {!formInputsValidity.street && <p>Street address cannot be empty!</p>}
      </div>
      <div className={zipControlClasses}>
        <label htmlFor="zip">Zip Code</label>
        <input type="text" id="zip" />
        {!formInputsValidity.zip && (
          <p>Please enter a valid zip code (5 characters long)!</p>
        )}
      </div>
      <div className={cityControlClasses}>
        <label htmlFor="city">City</label>
        <input type="text" id="city" />
        {!formInputsValidity.city && <p>City cannot be empty!</p>}
      </div>
      <div className={classes.actions}>
        <button type="button" onClick={props.onClose}>
          Cancel
        </button>
        <button className={classes.submit}>Confirm</button>
      </div>
    </form>
  );
};

export default CheckoutForm;
