import { useRef } from "react";
import classes from "./CheckoutForm.module.css";

const isEmpty = (value) => value.trim() === "";
const hasFiveDigits = (value) => value.trim().length === 5;

const CheckoutForm = (props) => {
  const nameRef = useRef();
  const streetRef = useRef();
  const zipRef = useRef();
  const cityRef = useRef();

  const confirmHandler = (event) => {
    event.preventDefault();
    const enteredName = nameRef.current.value;
    const enteredStreet = streetRef.current.value;
    const enteredZip = zipRef.current.value;
    const enteredCity = cityRef.current.value;

    const enteredNameIsValid = !isEmpty(enteredName);
    const enteredStreetIsValid = !isEmpty(enteredStreet);
    const enteredZipIsValid = hasFiveDigits(enteredZip);
    const enteredCityIsValid = !isEmpty(enteredCity);

    const formIsValid =
      enteredNameIsValid &&
      enteredStreetIsValid &&
      enteredZipIsValid &&
      enteredCityIsValid;

      if(formIsValid){
        
      } else {
        return;
      }
  };

//   const invalidNameClass = !enteredNameIsValid ? classes['control invalid'] : classes.control;
//   const invalidStreetClass = !enteredStreetIsValid ? classes['control invalid'] : classes.control;
//   const invalidZipClass = !enteredZipIsValid ? classes['control invalid'] : classes.control;
//   const invalidCityClass = !enteredCityIsValid ? classes['control invalid'] : classes.control;

  return (
    <form className={classes.form} onSubmit={confirmHandler}>
      <div className={classes.control}>
        <label htmlFor="name">Your Name</label>
        <input type="text" id="name" ref={nameRef} />
      </div>
      <div className={classes.control}>
        <label htmlFor="street">Street</label>
        <input type="text" id="street" ref={streetRef} />
      </div>
      <div className={classes.control}>
        <label htmlFor="zip">Postal Code</label>
        <input type="text" id="zip" ref={zipRef} />
      </div>
      <div className={classes.control}>
        <label htmlFor="city">City</label>
        <input type="text" id="city" ref={cityRef} />
      </div>
      <div className={classes.actions}>
        <button type="button" onClick={props.onClose}>
          Cancel
        </button>
        <button className={classes.submit}>Confirm</button>
      </div>
    </form>
  );
};

export default CheckoutForm;
