import React, {useState} from "react";
import classes from "./AvailableMeals.module.css";
import Card from "../UI/Card";
import MealItem from "./MealItem/MealItem";

const AvailableMeals = () => {
  const [isLoading, setIsLoading] = useState(false);
  
  const fetchMealsHandler = async () => {
    const response = await fetch('https://food-order-app-be8a0-default-rtdb.firebaseio.com/meals.json');
    const data = response.json();

    //pull all data into javascript object
    const meals = data.results.map(meal => {
      return {
        id: meal.id,
        name: meal.name,
        description: meal.description,
        price: meal.price
      }
    })

    return meals;
  }
  
  //helper function to make return statement leaner
  const mealsList = DUMMY_MEALS.map((meal) => (
    <MealItem
      id={meal.id}
      key={meal.id}
      name={meal.name}
      description={meal.description}
      price={meal.price}
    />
  ));

  return (
    <Card className={classes.meals}>
      <ul>{mealsList}</ul>
    </Card>
  );
};

export default AvailableMeals;
