import React, {Fragment, useState} from "react";
import classes from "./Cart.module.css";

/*
This component will act as a list for all items in
the user's cart. This component will be laid on top
of a custom reusable MODAL component.
*/

/*
We will definitely need STATE to manage our list

But what exactly do I need to do? I need to ultimately
map each object in an array to a <CartItem />

The user will be adding DIFFERENT TYPES of items one at
a time, but each type may have a different quantity value.

WHERE DOES THE ARRAY COME IN THO??
We need a STATE which will hold the array because
that's how the list can get updated.
*/


const Cart = (props) => {
  const [cartItems, setCartItems] = useState([]);
    const total = 0;

    const saveCartHandler = (item) => {
        setCartItems((prevItem) => {
            [item, ...prevItem];
        })
    }
  
    return (
    <Fragment>
      <ul className={classes["cart-items"]}>
        
      </ul>
      <div className={classes.actions}>
        <div className={classes.total}>
          <span>Total Amount</span>
          <span>{`$${total}`}</span>
        </div>
        <button onClick={props.onClose}>Close</button>
        <button>Order</button>
      </div>
    </Fragment>
  );
};

export default Cart;
