import React, { useReducer } from "react";

const CartContext = React.createContext({
  quantity: "1",
  totalQuantity: "1", //just only added this here
  
});

const cartReducerFunction = (state, action) => {
  const updatedTotalQuantity = state.totalQuantity + state.quantity;
  return { quantity: action.val, totalQuantity:  updatedTotalQuantity};
};

export const CartContextProvider = (props) => {
  /*
    CartItem props -> name, price, quantity, onRemove, onAdd

    useReducer will hold an object in our state snapshots w the following properties:
    -------------------------
    inputValue
    totalValue
    */

  const [cartItemState, dispatchCartItem] = useReducer(cartReducerFunction, {
    quantity: "1",
    totalQuantity: "1",
  });

  const addItemHandler = () => {};

  const removeItemHandler = () => {};

  return (
    <CartContext.Provider
      value={
        
        {
        quantity: cartItemState.quantity,
        totalQuantity: cartItemState.totalQuantity,
        // onDispatchCartItem: dispatchCartItem({val: ""}), //this is the line that is causing an infinite loop of re-renders
      }
    
    }
    >
      {props.children}
    </CartContext.Provider>
  );
};

export default CartContext;
