import React, { useReducer } from "react";

const CartItemContext = React.createContext({
  quantity: "1",
  totalQuantity: "1", //just only added this here
  //   cartItem: {
  //     name: "",
  //     price: "",
  //     quantity: "",
  //     onRemove: () => {},
  //     onAdd: () => {},
  //   },
  //   onSetInputValue: () => {},
});

const cartReducerFunction = (state, action) => {
  return { quantity: "1", totalQuantity: "0" };
};

export const CartItemContextProvider = (props) => {
  /*
    CartItem props -> name, price, quantity, onRemove, onAdd

    useReducer will hold an object in our state snapshots w the following properties:
    -------------------------
    inputValue
    totalValue
    */

  const [cartItemState, dispatchCartItem] = useReducer(cartReducerFunction, {
    quantity: "1",
    totalQuantity: "1",
  });

  const addItemHandler = () => {};

  const removeItemHandler = () => {};

  return (
    <CartItemContext.Provider
      value={{
        quantity: cartItemState.quantity,
        totalQuantity: cartItemState.totalQuantity,
        onDispatchCartItem: dispatchCartItem({}),

        // inputValue: inputValue,
        // onSetInputValue: setInputValue,
        // cartItem: {
        //   name: cartItemState.name,
        //   price: cartItemState.price,
        //   quantity: cartItemState.quantity,
        //   onRemove: removeItemHandler,
        //   onAdd: addItemHandler,
        // },
      }}
    >
      {props.children}
    </CartItemContext.Provider>
  );
};

export default CartItemContext;
