import React from "react";
import classes from "./MealItem.module.css";
import MealItemForm from "./MealItemForm";

const MealItem = (props) => {
  const price = `$${props.price.toFixed(2)}`;

  const createCartItemHandler = (event) => {
    event.preventDefault();
    /*
    we need to take all of the data here, package it
    in an object, and then put those objects into an
    array we can then pass to Cart.js
    */



  }
  
  return (
    <li className={classes.meal} key={props.id}>
      <div>
        <h3>{props.name}</h3>
        <div className={classes.description}>{props.description}</div>
        <div className={classes.price}>{price}</div>
      </div>
      <div>
      <MealItemForm id={props.id} type="submit" onSubmit={createCartItemHandler}/>
      </div>
      
    </li>
  );
};

export default MealItem;
