import React, {useState} from "react";

const CartItemContext = React.createContext({
    inputValue: '',
    onSetInputValue: () => {}
})


export const CartItemContextProvider = (props) => {
    const[inputValueState, setInputValueState] = useState('');

    const inputChangeHandler = (event) => {
        setInputValueState(event.target.value);
    }
    
    return <CartItemContext.Provider value={{
        inputValue: inputValueState,
        onSetInputValueState: inputChangeHandler
    }}>
        {props.children}
    </CartItemContext.Provider>
}

export default CartItemContext;
