import React, { Fragment, useContext } from "react";
import classes from "./Header.module.css";
import HeaderCartButton from "./HeaderCartButton";
import mainImg from "../../images/meals.jpg";
import Modal from "../UI/Modal";
import CartItemContext from "../../context/cart-context";

const Header = (props) => {
  /*
  I need to update the totalQuantity based on what we get
  from our cartItemContext inputValue.

  I can use a useReducer which will handle both the individual
  quantity as well as the total quantity, and our object itself.
  This useReducer can go in our context so we can use it anywhere.
  */
  
  const quantityContext = useContext(CartItemContext);

  return (
    <Fragment>
      
      <header className={classes.header}>
        <h1>ReactMeals</h1>
        <HeaderCartButton
          onClick={props.onShowCart}
          totalQuantity={quantityContext.totalQuantity}
        />
      </header>
      <div className={classes["main-image"]}>
        <img src={mainImg} alt="food laid out on buffet table" />
      </div>
    </Fragment>
  );
};

export default Header;
